-- ----------------------------
--  Table structure for `li_user_collect`
-- ----------------------------
DROP TABLE IF EXISTS `li_user_collect`;
CREATE TABLE `li_user_collect` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `url` varchar(255) DEFAULT NULL COMMENT '类型',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `author` varchar(255) DEFAULT NULL,
  `createtime` datetime DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL,
  `aid` int(11) DEFAULT NULL COMMENT '文章id',
  `iscollect` int(11) DEFAULT '1' COMMENT '是否收藏',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `session`
-- ----------------------------
DROP TABLE IF EXISTS `session`;
CREATE TABLE `session` (
  `tel` varchar(20) NOT NULL,
  `code` varchar(255) NOT NULL,
  `expire` bigint(20) NOT NULL,
  `length` int(5) NOT NULL,
  PRIMARY KEY (`tel`),
  KEY `expire` (`expire`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `user`
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `uid` char(20) COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '用户账号',
  `tel` char(11) COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '用户手机号',
  `pwd` char(40) COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '用户密码',
  `user_img` varchar(255) COLLATE utf8_bin NOT NULL COMMENT '用户头像',
  `nickname` char(16) COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '昵称',
  `sex` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '性别',
  `area` char(20) COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '用户地区',
  `token` char(20) COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT 'token',
  `login_length` bigint(20) NOT NULL DEFAULT '0' COMMENT '登录次数',
  `reg_time` datetime NOT NULL COMMENT '注册时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

SET FOREIGN_KEY_CHECKS = 1;
